mapper.insert:
insert
  into board
  (
    board_no
  , board_title
  , board_writer
  , board_content
  )
  values
  (
    seq_bno.nextval
  , #{boardTitle}
  , #{boardWriter}
  , #{boardContent}
  )

insert
  into attachment
  (
    file_no
  , file_path
  , filesystem_name
  , original_name
  , ref_type
  , ref_no
  )
  values
  (
    seq_ano.nextval
  , #{filePath}
  , #{filesystemName}
  , #{originalName}
  , #{refType}
  , seq_bno.currval
  )
